{"settings":"{
  // TEMP
  "editor.fontFamily": "JetBrains Mono, Consolas, 'Courier New', monospace",
  "editor.fontSize": 16,
  "editor.fontWeight": "300",
  "editor.lineHeight": 20,
  "editor.letterSpacing": 0.5,
  "editor.fontLigatures": true,
  "editor.wordWrap": "on",
  "editor.formatOnPaste": true,
  "editor.cursorBlinking": "smooth",
  // END TEMP
  //   "editor.lineHeight": 40,
  //   "editor.letterSpacing": 0,
  //   "terminal.integrated.fontSize": 10,
  "workbench.sideBar.location": "right",
  "workbench.startupEditor": "none",
  "explorer.confirmDelete": false,

  // editor
  // "editor.fontSize": 16,
  "editor.suggestFontSize": 16,
  "editor.suggestLineHeight": 28,
  "editor.formatOnSave": true,
  // "editor.fontWeight": "600",
  // "editor.fontFamily": "Tabular,Iosevka SS04 Medium,Fira Code",
  "editor.renderWhitespace": "all",
  "workbench.fontAliasing": "antialiased",
  "editor.quickSuggestionsDelay": 0,
  "workbench.editor.showTabs": false,
  "editor.accessibilitySupport": "off",
  "editor.bracketPairColorization.enabled": true,

  // terminal
  "terminal.integrated.fontSize": 13,
  "terminal.integrated.lineHeight": 1.5,
  "terminal.integrated.cursorBlinking": false,
  "terminal.integrated.cursorStyle": "line",
  "terminal.integrated.fontFamily": "Fira Code",

  // zen mode
  "zenMode.fullScreen": false,
  "editor.minimap.enabled": false,
  "editor.codeLens": false,

  // new window settings
  "window.newWindowDimensions": "offset",

  // git settings
  "diffEditor.renderSideBySide": false,
  "gitlens.blame.heatmap.enabled": false,
  "gitlens.blame.avatars": false,
  "gitlens.blame.compact": false,
  "gitlens.blame.separateLines": false,
  "gitlens.changes.locations": ["overview"],
  "gitlens.heatmap.locations": ["overview"],
  "scm.diffDecorations": "none",
  "scm.diffDecorationsGutterVisibility": "hover",
  "scm.diffDecorationsGutterAction": "none",

  // disable 'peek' previews when using 'goto' definition
  "editor.gotoLocation.multipleReferences": "goto",
  "editor.gotoLocation.multipleDefinitions": "goto",
  "editor.gotoLocation.multipleDeclarations": "goto",
  "editor.gotoLocation.multipleImplementations": "goto",
  "editor.gotoLocation.multipleTypeDefinitions": "goto",

  // helpers
  "editor.emptySelectionClipboard": false,
  "javascript.updateImportsOnFileMove.enabled": "always",
  "typescript.updateImportsOnFileMove.enabled": "always",

  // theme
  "workbench.colorTheme": "Gruvbox Material Dark",

  // search settings
  "search.exclude": {
    "**/node_modules": true,
    "**/bower_components": true,
    "**/*.code-search": true,
    "**/_site": true,
    "_site": true,
    "**/build": true,
    "**/bin": true,
    "**/*.svg": true
  },

  // file explorer settings
  "files.exclude": {
    "**/.git": true,
    "**/.svn": true,
    "**/.hg": true,
    "**/CVS": true,
    "**/.DS_Store": true,
    "**/node_modules": true,
    "**/build": true,
    "USE_GITIGNORE": true
  },

  // auto rename tag
  "auto-rename-tag.activationOnLanguage": [
    "html",
    "xml",
    "php",
    "typescript-react",
    "typescriptreact",
    "tsx"
  ],

  // sync settings
  "settingsSync.keybindingsPerPlatform": false,

  // language specific settings
  // svelte
  "[svelte]": {
    "editor.defaultFormatter": "svelte.svelte-vscode"
  },
  "[typescript]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "[typescriptreact]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "[jsonc]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "[javascript]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "[prisma]": {
    "editor.defaultFormatter": "Prisma.prisma"
  },
  "[json]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "[csharp]": {
    "editor.defaultFormatter": "ms-dotnettools.csharp"
  },

  // VIM SETTINGS
  "vim.easymotion": true,
  "vim.incsearch": true,
  "vim.useSystemClipboard": true,
  "vim.useCtrlKeys": true,
  "vim.hlsearch": true,
  "vim.insertModeKeyBindings": [
    {
      "before": ["j", "j"],
      "after": ["<Esc>"]
    }
  ],
  "vim.normalModeKeyBindingsNonRecursive": [
    {
      "before": ["<leader>", "d"],
      "after": ["d", "d"]
    },
    {
      "before": ["<C-n>"],
      "commands": [":nohl"]
    }
  ],
  "vim.leader": "<space>",
  "vim.handleKeys": {
    "<C-a>": false,
    "<C-f>": false
  },
  "omnisharp.organizeImportsOnFormat": true,
  "workbench.editor.enablePreview": false,
  "breadcrumbs.enabled": false,
  "go.toolsManagement.autoUpdate": true,
  "vim.enableNeovim": true,
  "vim.smartRelativeLine": true,
  "workbench.activityBar.visible": false,
  "[vue]": {
    "editor.defaultFormatter": "octref.vetur"
  }
}"}
